import React from 'react';
import { Col, Button, Form, FormGroup, Label, Input, FormText } from 'reactstrap';

export default class Example extends React.Component {
    constructor(props) {
      super(props);
      this.state = {
        fname: '',
        lname: '',
        email: '',
        message: '',
      }
    }

    handleFormSubmit( event ) {
      event.preventDefault();
      console.log(this.state);
    }

  render() {
    return (
    <div>
        <p>Contact Us</p>
      <Form className="App">
      <FormGroup row style={{paddingTop: 30}}>
        <Label for="exampleText" sm={2}>First Name</Label>
        <Col sm={10}>
          <Input type="text" name="firstname" id="fname"
          value={this.state.fname}
          onChange={e => this.setState({ fname: e.target.value })}/>
        </Col>
      </FormGroup>
      <FormGroup row style={{paddingTop: 30}}>
        <Label for="exampleText" sm={2}>Last Name</Label>
        <Col sm={10}>
          <Input type="text" name="lastname" id="lname" 
          value={this.state.lname}
onChange={e => this.setState({ lname: e.target.value })}/>
        </Col>
      </FormGroup>
        <FormGroup row style={{paddingTop: 30}}>
          <Label for="exampleEmail" sm={2}>Email</Label>
          <Col sm={10}>
            <Input type="email" name="email" id="exampleEmail" />
          </Col>
        </FormGroup>
        <FormGroup row style={{paddingTop: 30}}>
          <Label for="exampleText" sm={2}>Message</Label>
          <Col sm={10}>
            <Input type="textarea" name="text" id="exampleText" />
          </Col>
        </FormGroup>
        <FormGroup row style={{paddingTop: 30}} className="padded">
          <Col sm={{ size: 10, offset: 0 }}>
            <Button>Submit</Button>
          </Col>
        </FormGroup>
      </Form>
      </div>
    );
  }
}
